/* --------------------------------------------------*/
/*        Switch according to the environment        */
const baseUrl = new URL('https://linq.trap.games')
// const baseUrl = new URL('http://localhost:3002')
/* --------------------------------------------------*/

const baseApiUrl = new URL('/api/entry', baseUrl)
const iconUrl =
  'data:image/png;base64,'

const notify = async (title: string, message: string, url: URL) => {
  const options: chrome.notifications.NotificationOptions = {
    type: 'basic',
    title,
    message,
    iconUrl,
  }

  try {
    const res = await Notification.requestPermission()
    if (res !== 'granted') {
      return
    }

    chrome.notifications.onClicked.addListener(() => {
      const newUrl = new URL(`/entry?url=${url.toString()}`, baseUrl)
      void chrome.tabs.create({ url: newUrl.toString() })
    })
    chrome.notifications.create('create', options)
  } catch (err) {
    // eslint-disable-next-line @typescript-eslint/restrict-template-expressions
    alert(`${title}\n${message}\n(desktop notification rejected: ${err})`)
  }
}

const getTags = () => {
  const tags: string[] = []
  let tag = prompt('タグを追加(空文字で終了)')
  while (tag) {
    tags.push(tag)
    tag = prompt('タグを追加(空文字で終了)')
  }
  return tags
}

const saveToLinq = async (tab: chrome.tabs.Tab, tags: string[]) => {
  if (tab.url === undefined) {
    return
  }
  if (tab.url.startsWith('chrome://')) {
    await chrome.tabs.create({ url: baseUrl.toString() })
    return
  }
  const url = new URL(tab.url, baseApiUrl)
  const title: string = tab.title ? tab.title : ''

  if (!url.toString().startsWith('http')) {
    return
  }

  const query = { url, title, tags }

  const param = {
    method: 'PUT',
    body: JSON.stringify(query),
    headers: {
      'Content-Type': 'application/json',
    },
  }

  try {
    const res = await fetch(baseApiUrl.toString(), param)
    // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
    const data = await res.json()

    console.log(JSON.stringify(data))
    await notify('Page Bookmarked!', title, url)
  } catch (err) {
    console.log(err)
    // eslint-disable-next-line @typescript-eslint/restrict-template-expressions
    await notify('Bookmark Failed...', `${err}`, url)
  }
}

// インストール時に実行
chrome.runtime.onInstalled.addListener(() => {
  chrome.contextMenus.create({
    id: 'saveToLinq',
    title: 'Save to linQ',
  })
})

// ショートカットキーをプッシュ時に実行
chrome.commands.onCommand.addListener(
  (command: string, tab: chrome.tabs.Tab) => {
    if (command === 'save') {
      void saveToLinq(tab, [])
    } else if (command === 'saveWithTags') {
      const tags = getTags()
      if (tags.length > 0) {
        void saveToLinq(tab, tags)
      }
    }
  }
)

// メニューをクリック時に実行
chrome.contextMenus.onClicked.addListener((_, tab) => {
  if (tab !== undefined) {
    void saveToLinq(tab, [])
  }
})
